import  {
    GET_POSITIONS_REQUEST, GET_POSITIONS_FAIL, GET_POSITIONS_SUCCESS,
    GET_POSITION_REQUEST, GET_POSITION_FAIL, GET_POSITION_SUCCESS,
    ADD_POSITION_REQUEST, ADD_POSITION_FAIL, ADD_POSITION_SUCCESS,
    EDIT_POSITION_REQUEST, EDIT_POSITION_FAIL, EDIT_POSITION_SUCCESS,
    DELETE_POSITION_REQUEST, DELETE_POSITION_FAIL, DELETE_POSITION_SUCCESS,
    SET_POSITION_MODAL,HANDLE,TOGGLE_POSITION_MODAL
} from "./actionTypes";

const INIT_STATE = {
    positions: [],
    position: {},
    modal: {},
    loading: false,
    success: false,
    fail: false,
    errors: {}
};

export default (state = INIT_STATE, action) => {
    switch (action.type) {
        case GET_POSITIONS_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: {},
            };
        case GET_POSITIONS_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,
            };
        case GET_POSITIONS_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                positions: JSON.parse(action.result.data),
                errors: {},
            };
        case GET_POSITION_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: {},
            };
        case GET_POSITION_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,
            };
        case GET_POSITION_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                position: JSON.parse(action.result.data),
                errors: {},
            };
        case ADD_POSITION_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
            }
        case ADD_POSITION_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case ADD_POSITION_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: false,
                positions: [
                    ...state.positions,
                    JSON.parse(action.result.data)

                ]
            };
        case EDIT_POSITION_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
            }
        case EDIT_POSITION_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case EDIT_POSITION_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: false,
                position: {},
                positions: JSON.parse(action.result.data)
            };
        case DELETE_POSITION_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
            }
        case DELETE_POSITION_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case DELETE_POSITION_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: false,
                position: {},
                positions: JSON.parse(action.result.data)
            };
        case SET_POSITION_MODAL:
            state.position[action.key] = action.value
            return {
                ...state,
            };
        case TOGGLE_POSITION_MODAL:
            let newModal = {};
            newModal[action.modalType] = !state.modal[action.modalType];
            let position = (action.modalType==="edit")?{
                ...state.position,
                ...action.obj
            }:{
                ...action.obj
            }
            return {
                ...state,
                modal: {
                    ...state.modal,
                    ...newModal
                },
                position
            }
        case HANDLE:
            if (!state.position.perm) {
                state.position.perm = {}
            }
            let perm = state.position.perm;
            if (action.parentId) {
                if (perm[action.parentId]) {
                    let index = perm[action.parentId].indexOf(action.id);
                    if (index !== -1) {
                        perm[action.parentId].splice(index, 1);
                        if (perm[action.parentId].length === 0) {
                            delete perm[action.parentId];
                        }
                    } else {
                        perm[action.parentId].push(action.id)
                    }
                } else {
                    perm[action.parentId] = [action.id]
                }
            } else {
                if (perm[action.id]) {
                    delete perm[action.id];
                } else {
                    perm[action.id] = [];
                }

            }
            return {
                ...state,
                position: {
                    id: state.position.id,
                    name: state.position.name,
                    perm: perm
                }
            }

        default:
            return {...state};
    }
};
