import React, {Component, useState} from "react";
import {Row, Col, Modal, ModalHeader, ModalBody, ModalFooter, Button, Table} from "reactstrap";
import {ChevronDown, ChevronRight} from "react-feather";
import PlusButton from "../../../components/buttons/plusButton";
import AddButton from "../../../components/buttons/addButton";
import EditButton from "../../../components/buttons/editButton";
import DeleteButton from "../../../components/buttons/deleteButton";
import Translate from "../../../Translate";
import ModalComponent from "../items/itemModal";

function SubMenu(props) {
    const [open, setOpen] = useState(false);

    function handleOpen() {
        setOpen(!open)
    }

    function SubMenuItem(props) {
        if (open && props.data) {
            return <Menu data={props.data}/>
        }
        return <div></div>
    }

    function Chevron(props) {
        if (props.data) {
            if (!open) {
                return <ChevronRight size={18} className="mr-2" onClick={() => handleOpen()}/>
            }
            return <ChevronDown size={18} className="mr-2" onClick={() => handleOpen()}/>
        }
        return <div></div>
    }

    return (
        <li className={"list-group-item list-group-item-action  py-1"}>
            <Row className="todo-list-group-item">
                <Col sm="9" md="9" onClick={() => handleOpen()}><Chevron data={props.data.children}/>{props.data.name}
                </Col>
                <Col sm="3" md="3">
                    <PlusButton perm={props.perm}/>
                    <EditButton perm={props.perm}/>
                    <DeleteButton perm={props.perm}/>
                </Col>
            </Row>
            <SubMenuItem data={props.data.children}/>
        </li>
    )


};

function Menu(props) {
    if (props.data.length > 0) {
        return <ul className="list-group ">
            {props.data.map((value, index) => {
                return (
                    <SubMenu data={value}/>
                )
            })}

        </ul>

    } else {
        return <div></div>
    }
}

function categoryTable(props) {
    function toggle(type,id) {
        props.itemModal(type,!props.modal[type])
        // if(id){
        //     props.getItems(id)
        // }
    }
    function handleClick() {
        props.deleteCategory(props.user.id)
    }
    return (
        <div>

            <Menu data={props.data}/>
            <Modal isOpen={props.modal.edit} toggle={()=>toggle("edit")}  size="md">
                <ModalHeader toggle={()=>toggle("edit")}><Translate name="editPosition"/></ModalHeader>
                <ModalComponent {...props}/>
            </Modal>
            <Modal isOpen={props.modal.delete} toggle={()=>toggle("delete")}  size="md">
                <ModalHeader toggle={()=>toggle("delete")}><Translate name="deletePosition"/></ModalHeader>
                <ModalBody>Դուք համոզված ե՞ք ջնջել</ModalBody>
                <ModalFooter>
                    <Button color="primary" type="submit" onClick={handleClick}>
                        <Translate name="confirm"/>
                    </Button>
                </ModalFooter>
            </Modal>

        </div>

    );
}

export default categoryTable;
