import SessionStorage from '../services/SessionStorage';


export const saveSession = ({accessToken, expireAt, refreshToken, user, id}, withUser = true) => {
    const options = {
        path: '/',
        expires: new Date( Date.now() + expireAt*1000),
    };
    console.log('token', accessToken, options)
    SessionStorage.set('token', accessToken, options);
    if (withUser) {
        SessionStorage.set('user', user, options);
    }
};

export const getSession = (key) => {
    SessionStorage.get(key);
}
