import React from "react";
import {Col, Row, FormGroup, Label, Button, ModalBody, ModalFooter, Input, Modal, ModalHeader} from "reactstrap";
import Translate from "../../../Translate";
import {Formik, Field, Form} from "formik";
import * as Yup from "yup";
import jwt from "jwt-simple";

const UserModal = (props) => {
    const formSchema = Yup.object().shape({
        firstname: Yup.string()
            .required("Required"),
        username: Yup.string()
            // .email("Invalid email")
            .required("Required"),
        password: Yup.number()
            .min(4, "Too Short!")
            .required("Required")
    });

    function modalBodyContent() {
        if (props.type === "delete") {
            return (
                <ModalBody>Դուք համոզված ե՞ք ջնջել</ModalBody>
            )

        } else {
            return (
                <ModalBody>
                    <Formik
                        initialValues={props.user}
                        validationSchema={formSchema}
                        onSubmit={values => {
                            // same shape as initial values
                            console.log(values);
                        }}
                    >
                        {({errors, touched}) => (

                            <Form>
                                {
                                    console.log(errors)
                                    // console.log(touched)
                                }
                                <Row>
                                    <Col md={6}>
                                        <FormGroup>
                                            <Label for="firstName"><Translate name="firstname"/></Label>
                                            {/*<input*/}
                                            {/*    className="form-control"*/}
                                            {/*    type="text"*/}
                                            {/*    id="firstName"*/}
                                            {/*    value={props.user.firstname}*/}
                                            {/*    onChange={event => props.setModalValues("firstname", event.target.value)}*/}

                                            {/*/>*/}
                                            <Field
                                                name="firstname"
                                                id="firstName"
                                                className={`form-control ${errors.firstname && props.user.firstname=="" && 'is-invalid'}`}
                                                value={props.user.firstname}
                                                onChange={event => props.setModalValues("firstname", event.target.value)}
                                            />
                                            {errors.firstname && props.user.firstname ? <div className="invalid-feedback">{errors.firstname}</div> : null}
                                        </FormGroup>
                                    </Col>
                                    <Col md={6}>
                                        <FormGroup>
                                            <Label for="lastName"><Translate name="lastname"/></Label>
                                            <input
                                                className="form-control"
                                                type="text"
                                                id="lastName"
                                                onChange={event => props.setModalValues("lastname", event.target.value)}
                                                value={props.user.lastname ? props.user.lastname : ""}
                                            />
                                        </FormGroup>
                                    </Col>
                                </Row>
                                <Row>
                                    <Col md={6}>
                                        <FormGroup>
                                            <Label for="username"><Translate name="username"/></Label>
                                            <input
                                                className="form-control"
                                                type="text"
                                                id="username"
                                                onChange={event => props.setModalValues("username", event.target.value)}
                                                value={props.user.username ? props.user.username : ""}
                                                required
                                            />
                                        </FormGroup>
                                    </Col>
                                    <Col md={6}>
                                        <FormGroup>
                                            <Label for="password"><Translate name="password"/></Label>
                                            <input
                                                className="form-control"
                                                type="password"
                                                id="password"
                                                onChange={event => props.setModalValues("password", jwt.encode(event.target.value, "password"))}
                                                value={props.user.password ? jwt.decode(props.user.password, "password") : ""}
                                                required
                                            />
                                        </FormGroup>
                                    </Col>
                                </Row>
                                <Row>
                                    <Col md={6}>
                                        <FormGroup>
                                            <Label for="position"><Translate name="position"/></Label>
                                            <Input type="select" id="position"
                                                   defaultValue={props.user.position ? props.user.position : 0}
                                                   onChange={event => props.setModalValues("position_id", event.target.value)}>
                                                <option key={0}>Ընտրել</option>
                                                {props.positions.map((item, key) => {
                                                    return <option value={item.id} key={key}
                                                                   selected={item.id === parseInt(props.user.position_id)}>{item.name}</option>
                                                })}
                                            </Input>
                                        </FormGroup>
                                    </Col>
                                    <Col md={6}>
                                        <FormGroup>
                                            <Label for="active"><Translate name="active"/></Label>
                                            <Input type="select" id="active" defaultValue={0}
                                                   onChange={event => props.setModalValues("active", event.target.value)}>

                                                <option value="0" selected={0 === props.user.active}>Այո</option>
                                                <option value="1" selected={1 === props.user.active}>Ոչ</option>
                                            </Input>
                                        </FormGroup>
                                    </Col>
                                </Row>
                            </Form>
                        )}
                    </Formik>

                </ModalBody>
            )

        }

    }

    return (
        <React.Fragment>
            <Modal isOpen={props.modal[props.type]} toggle={() => props.toggleModal(props.type)} size="md">
                <ModalHeader toggle={() => props.toggleModal(props.type)}><Translate name="addUser"/></ModalHeader>
                {modalBodyContent()}
                <ModalFooter>
                    <Button color="primary" type="submit" onClick={function () {
                        props.userActions(props.type, props.user);
                    }}>
                        <Translate name="confirm"/>
                    </Button>
                </ModalFooter>
            </Modal>

        </React.Fragment>
    );

};

export default UserModal;
