import {
    GET_USERS_REQUEST,
    GET_USERS_FAIL,
    GET_USERS_SUCCESS,
    ADD_USERS_REQUEST,
    ADD_USERS_FAIL,
    ADD_USERS_SUCCESS
} from "./actionTypes";


const INIT_STATE = {
    users: [],
    loading: false,
    success: false,
    fail: false,
    errors: {},
    cols: {
        
    }
};

export default (state = INIT_STATE, action) => {
    switch (action.type) {
        case GET_USERS_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: {},
            };
        case GET_USERS_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,
            };
        case GET_USERS_SUCCESS:
            let data = JSON.parse(action.result.data)
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                users: data,
                errors: {},
            };
        case ADD_USERS_REQUEST:
            return {
                ...state,
                logout: {
                    ...state.logout,
                    loading: true,
                    success: false,
                    fail: false,
                }
            };
        case ADD_USERS_FAIL:
            return {
                ...state,
                logout: {
                    ...state.logout,
                    loading: false,
                    success: true,
                    fail: false,
                },
                users: null,
            };
        case ADD_USERS_SUCCESS:
            return {
                ...state,
                logout: {
                    ...state.logout,
                    loading: false,
                    success: false,
                    fail: true,
                },
            };

        default:
            return {...state};
    }
};
