    return (
        <li className={"list-group-item list-group-item-action no-border  bg-lighten-4"} key={props.id}>
            <Row className="todo-list-group-item">
                <Col sm="1" md="1"><Label check onClick={() => setChecked(!checked)} onClick={() => handle}><CustomInput type="checkbox" defaultChecked={true}/></Label></Col>
