import {ADD_FILE, SET_FORM_DATA_VALUE} from "./actionTypes";

export function addFile(name, value) {

    return {
        type: ADD_FILE,
        name,
        value
    }
}

export function changeFormDataValue(name, value, area) {

    return (dispatch, getState) => {
        const formDataValue = {...getState().importReducer.formDataValue};
        const formData = {...getState().importReducer.formData};
        formData.area.name.touched = true
        if (value.length > 0) {
            formData.area.name.valid = true
            formDataValue[name] = value
        }
        dispatch(setFormDataValue(formDataValue))
    }
}

export function setFormDataValue(formDataValue) {

    return {
        type: SET_FORM_DATA_VALUE,
        formDataValue
    }
}
