export const IsJsonString = (str) => {
    try {
        JSON.parse(str);
    } catch (e) {
        return false;
    }
    return true;
};

export const IsRequiredField = (state,field=false) => {
    if(field){
        if(state.required.includes(field.key) && field.value===""){
            state.errors[field.key] = "Required";
        }else{
            delete state.errors[field.key];
        }
    }

    return state.errors;
}
