import React from 'react';
import {Input, Table, InputGroup, Button} from 'reactstrap';
import Translate from "../../../../../../Translate";
import {Settings, Plus, Trash2} from "react-feather";
import DropdownComponent from "../../../../../../components/dropdown/dropdown";


export default class Example extends React.Component {

    state = {
        currentInputValue: {
            barecode: "",
            type: null
        },
        error: false
    }

    addBarcode() {
        if (this.state.currentInputValue.barecode !== "") {
            this.props.barcodeActions("add", this.state.currentInputValue);
            let newCurrentInput = {...this.state.currentInputValue};
            newCurrentInput.barecode = "";
            this.setState({currentInputValue: newCurrentInput, error: false})
        } else {
            this.setState({error: true})
        }
    }

    deleteBarcode(code) {
        this.props.barcodeActions("delete", code);
    }

    changeBarcode(value) {
        let newCurrentInput = {...this.state.currentInputValue};
        newCurrentInput.barecode = value;
        this.setState({currentInputValue: newCurrentInput})
    }

    render() {
        console.log('this state', this.state.currentInputValue)
        let props = this.props;
        return (
            <Table borderless>
                <thead>
                <tr>
                    <th><Translate name={"barcode"}/></th>
                    <th><Settings size={20}></Settings></th>
                </tr>
                </thead>
                <tbody>
                {this.props.product.barcode && this.props.product.barcode.map(function (value, index) {
                    return (
                        <tr key={index}>
                            <td>{value.barcode.barecode} : {value.barecode.type}</td>
                            <td>
                                <Button size="sm" color="danger" className="mb-0" onClick={() => {
                                    props.barcodeActions("delete", value.barcode.barecode)
                                }}>
                                    <Trash2 size={16}/>
                                </Button>
                            </td>
                        </tr>
                    )
                })}
                <tr>
                    <td>
                        <InputGroup size="sm">
                            <Input
                                type="text"
                                className={`form-control  ${this.state.error ? 'is-invalid' : ''}`}
                                id="barcode"
                                value={this.state.currentInputValue.barecode ? this.state.currentInputValue.barecode || '' : ''}
                                onChange={event => this.changeBarcode(event.target.value)}
                            />
                            <DropdownComponent
                                setBarcodeType={this.props.setBarcodeType}
                                data={this.props.barcodeTypes}
                                onChange={this.props.setModalValues}
                                name='barcodeType'
                            />
                        </InputGroup>
                    </td>
                    <td>
                        <Button size="sm" color="primary" onClick={() => {
                            this.addBarcode()
                        }}>
                            <Plus size={16}/>
                        </Button>
                    </td>
                </tr>
                </tbody>

            </Table>
        );
    }
}
