import React, {Component} from "react";
import {Col, Card, CardBody, ButtonGroup} from "reactstrap";
import TableComponent from './translationsTable';
import ModalComponent from './translationsModal';
import AddButton from "../../../components/buttons/addButton";
import MyButton from "../../../components/buttons/button";
// import {toggleTranslationModal, translationActions} from "../../../redux/lang/actions";


class MenuContainer extends Component {

    componentDidMount() {
        this.props.getTranslations()
        this.props.getTranslationsWithSize(10)
    }

    render() {
        return (
            <Col sm="12">
                <Card>
                    <CardBody>
                        <ButtonGroup>
                            <MyButton
                                name={'Հայերեն - am'}
                                type={'info'}
                                outline={true}
                                oncClick={
                                    this.getTranslationWithLang.bind(this, 'am', this.props.activePage, this.props.itemsCountPerPage)
                                }
                            />
                            <MyButton
                                name={'Русский - ru'}
                                type={'info'}
                                outline={true}
                                oncClick={
                                    this.getTranslationWithLang.bind(this, 'ru', this.props.activePage, this.props.itemsCountPerPage)
                                }
                            />
                            <MyButton
                                name={'English - en'}
                                type={'info'}
                                outline={true}
                                oncClick={
                                    this.getTranslationWithLang.bind(this, 'us', this.props.activePage, this.props.itemsCountPerPage)
                                }
                            />
                        </ButtonGroup>
                        <TableComponent
                            getTranslationPage={this.props.getTranslationPage}
                            itemsCountPerPage={this.props.itemsCountPerPage}
                            pageRangeDisplayed={this.props.pageRangeDisplayed}
                            totalItemsCount={this.props.totalItemsCount}
                            activePage={this.props.activePage}
                            setCount={this.props.setCount}
                            data={this.props.translationsSize}
                            toggleModal={this.props.toggleTranslationModal}
                            actions={this.props.translationActions}
                            // perm={this.props.perm}
                         />
                    </CardBody>
                </Card>
                <ModalComponent {...this.props} type={'add'}/>
                <ModalComponent {...this.props} type={'edit'}/>
                <ModalComponent {...this.props} type={'delete'}/>


            </Col>
        )
    }
}


export default MenuContainer;
