import React, {Component} from "react";
import {Badge} from "reactstrap";
import {connect} from "react-redux";

class LocalizeTab extends Component {
    constructor(props) {
        super(props)
        this.state = {
            code :'am'
        }
    }
    handleClick = (code) =>{
        this.setState(code)
    }
    render() {
        console.log(this.props.languages)
        let state = this.state
        return (
                <div className="badge-toolbar">

                    {this.props.languages.map(function (lang) {
                        return <Badge  color={state.code ===lang.code ? "primary" : "light" } onClick = {this.handleClick(lang.code)}>
                            {lang.name}
                        </Badge>
                    })}
                </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        languages: state.languages.languages,

    }
};

export default connect(mapStateToProps)(LocalizeTab);
