import React from "react";
import Translate from "../../Translate";
import {Button} from "reactstrap";

export default function AddButton(props) {
    let unauthorized  = (props.perm)?props.perm.includes("add"):false;
    function toggle() {
        props.userModal("add",!props.modal.add)
    }
    return unauthorized ? <div></div> : 
    <div>
        <Button
            color="primary"
            type="button"
            className="btn btn-raised btn-danger my-2 shadow-z-2"
            onClick={() => toggle()}
        >
            <Translate name="add"/>
        </Button>
        <Modal isOpen={props.modal.add}  size="md">
            <ModalHeader toggle={()=>this.toggle()}><Translate name="addContact"/></ModalHeader>
            <UserModal {...this.props}/>
        </Modal>

    </div>
   ;
}
