import React from "react";
import {Table/*, Input */} from "reactstrap";
import Translate from "../../../Translate";
import EditButton from "../../../components/buttons/editButton";
import DeleteButton from "../../../components/buttons/deleteButton";

/*name change example to TableComponent*/

export default class TableComponent extends React.Component {
    render() {
        return (
            <Table responsive>
                <thead>
                <tr>
                    {Object.keys(this.props.data[0]).map((key) => <td key={key}><Translate name={key}/></td>)}
                    <td>E/D</td>
                </tr>
                </thead>
                <tbody>
                {
                    this.props.data.map(
                        item => {

                            return (
                                <tr key={item.id}>
                                    {Object.keys(item).map((key) => <td key={key}>{item[key]}</td>)}
                                    <td>
                                        <EditButton perm = {'Edit'} onClick={function(){ this.props.toggleModal('edit',item.id); }} />
                                        <DeleteButton perm = {'Delete'} onClick={() => this.props.toggleModal('delete',item.id)}/>
                                    </td>
                                </tr>

                            )
                        }
                    )
                }
                </tbody>
            </Table>

        );
    }
}
