import React, { useState } from "react";
import {Col, Row, FormGroup, Label, Button, ModalBody, ModalFooter, CustomInput} from "reactstrap";
import {ChevronDown ,ChevronRight} from 'react-feather';
import Translate from "../../Translate";
// import MenuItem from "./menuItem";
const DropDownItemChild = (props) => {
    const [checked,setValue] = useState(props.checked);
    function handleChecked() {
        setValue(!checked)
    }
    return (
        <li className={"list-group-item list-group-item-action no-border  bg-lighten-4"} key={id}>
                        <Row className="todo-list-group-item" >
                            <Col sm="1" md="1" ><Label  onClick={() => handleChecked() }><CustomInput type="checkbox"  checked = {props.tools[id].checked} defaultChecked={props.tools[id].checked}/></Label></Col>
                            <Col sm="10" md="10" onClick={() => handleOpen()}><p className="mb-0 font-small-3" ><Translate name={props.tools[id].name}/></p></Col>
                        </Row>
                    </li>
    );
}

const DropDownItem = (props) => {
    const [checked,setValue] = useState(props.checked);
    const [open,setOpen] = useState(false);
    function handleChecked() {
        setValue(!checked)
    }
    function handleOpen() {
        setOpen(!open)
    }
    function Greeting(props) {
        if(!open){
            return <ChevronRight size={18} className="mr-2" onClick={() => handleOpen()}/>
        }
        return <ChevronDown size={18} className="mr-2" onClick={() => handleOpen()}/>
    }

    return (
        <li className={"list-group-item list-group-item-action no-border  bg-lighten-4"} key={props.id}>
            <Row className="todo-list-group-item" >
                <Col sm="1" md="1" ><Label  onClick={() => handleChecked() }><CustomInput type="checkbox"  checked = {checked} defaultChecked={checked}/></Label></Col>
                <Col sm="10" md="10" onClick={() => handleOpen()}><p className="mb-0 font-small-3" ><Translate name={props.name}/></p></Col>
                <Col sm="1" md="1">
                    <Greeting />
                </Col>
            </Row>
            <ul className="list-group icheck-task hide">
            {Object.keys(props.tools).map(function(key) {
                if(props.tools[key].checked === undefined){
                    props.tools[key].checked = true
                }
                console.log(props.tools[key])
                // return <li className={"list-group-item list-group-item-action no-border  bg-lighten-4"} key={id}>
                //     <Row className="todo-list-group-item" >
                //         <Col sm="1" md="1" ><Label  onClick={() => handleChecked() }><CustomInput type="checkbox"  checked = {props.tools[id].checked} defaultChecked={props.tools[id].checked}/></Label></Col>
                //         <Col sm="10" md="10" onClick={() => handleOpen()}><p className="mb-0 font-small-3" ><Translate name={props.tools[id].name}/></p></Col>
                //     </Row>
                // </li>
                return <DropDownItemChild id = {key} key={key} name = {props.tools[key].name}  checked = {props.tools[key].checked}/>


            })}
            </ul>
        </li>
    );
};

const DropDown = (props) => {
    let pages = props.pages;

    function handle(key) {
        console.log(key)
    }

    return (
        <ul className="list-group icheck-task">
            {Object.keys(pages).map(function (key) {
                let checked = true;
                if (pages[key].checked) {
                    checked = pages[key].checked
                }
                return <DropDownItem id = {key} key={key} name = {pages[key].name} tools = {pages[key].tools} checked = {checked}/>



            })}
        </ul>


    );

};
export default DropDown;
