import {
    ADD_TRANSLATION,
    INITIALIZE
} from "./actionTypes";
import { initialize,addTranslation } from 'react-localize-redux';
import { translations } from './translation'



const languages = [
    { name: 'English', code: 'en' },
    { name: 'French', code: 'fr' },
    { name: 'Spanish', code: 'es' }
];

export default (state = languages, action) => {

    switch (action.type) {
        case ADD_TRANSLATION:
            addTranslation(translations)
            return {
                loading: true,
            };
        case INITIALIZE:
            (languages, { defaultLanguage: 'fr' })
            return {
                initialize: true,
            };
        default:
            return {...state};
    }
};
