import React from "react";
import {connect} from "react-redux";
import {bindActionCreators} from "redux";
import {checkTranslations} from './redux/lang/actions'


function Translate(props) {
    const checkWord = () => {
        if (props.translations[props.name]) {
            return props.translations[props.name]
        }
        props.checkTranslations(props.name.toLowerCase(), props.name);
        return props.name;
    }
    return (
        <span>
            {checkWord()}
        </span>
    );
}

const mapDispatchToProps = dispatch => {
    return bindActionCreators(
        {
            checkTranslations
        },
        dispatch
    );
};

const mapStateToProps = state => {
    return {
        translations: state.languages.translations,
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Translate);
