import React, {Component} from "react";
import {Card, CardBody, CardHeader, Col, Row} from "reactstrap";
import UploadButton from "../../../components/buttons/upploadBtnUI";
import AttachFileIcon from '@material-ui/icons/AttachFile';
import CustomizedSnackbars from "../../../components/snachbarsUI/snachbarsUi";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import StarIcon from '@material-ui/icons/Star';
import FileCopyIcon from '@material-ui/icons/FileCopy';
import MemoryIcon from '@material-ui/icons/Memory';
import Translate from "../../../Translate";

class ImportPage extends Component {
    state = {
        exel: false,
        file: null,
        errorType: null,
        uploadText: ''
    }

    handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }

        this.setState({
            exel: false,
        })
    };

    importExelHandler = event => {
        let file = event.target.files[0];
        if (file.type === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet') {
            this.setState({
                exel: true,
                file: event.target.files[0],
                uploadText: 'your file added successfully',
                errorType: 'success'
            })
        } else {
            console.log(event.target)
            // event.target..reset()
            this.setState({
                exel: true,
                file: null,
                uploadText: 'file type is not supported, make sure your file is Excel',
                errorType: 'error'
            })
        }

    }

    render() {
        console.log(this.state.exel)
        return (
            <Row>
                <Col sm={12}>
                    <Card>
                        <CardHeader>
                            <UploadButton
                                icon={<AttachFileIcon/>}
                                variant={'contained'}
                                color={'primary'}
                                multiple={false}
                                onChange={this.importExelHandler}
                                accept={'".csv, application/vnd.openxmlformats-officedocument.spreadsheetml.sheet, application/vnd.ms-excel'}
                                padding={'5px 15px'}
                                margin={'2px 2px 10px'}
                            />
                            <List component="nav" aria-label="contacts">
                                <ListItem>
                                    <ListItemIcon>
                                        <FileCopyIcon />
                                    </ListItemIcon>
                                    <ListItemText primary={<Translate name={'File name'}/>} />
                                    {
                                        this.state.file ?
                                            this.state.file.name
                                            :
                                            null
                                    }
                                </ListItem>
                                <ListItem>
                                    <ListItemIcon>
                                        <MemoryIcon />
                                    </ListItemIcon>
                                    <ListItemText primary={<Translate name={'File size'}/>} />
                                </ListItem>
                            </List>
                        </CardHeader>
                        <CardBody>
                        </CardBody>
                        <CustomizedSnackbars
                            open={this.state.exel}
                            severity={this.state.errorType}
                            text={this.state.uploadText}
                            handleClose={this.handleClose}
                        />
                    </Card>
                </Col>
            </Row>
        )
    }
}

export default ImportPage
