import React from 'react';
import {Input, Table, InputGroup, Button} from 'reactstrap';
import Translate from "../../../../../../Translate";
import {Settings, Plus, Trash2} from "react-feather";
import DropdownComponent from "../../../../../../components/dropdown/dropdown";


export default class Example extends React.Component {
    state = {
        currentInputValue:""
    }
    setInputValue(newValue){
        this.setState({currentInputValue:newValue})
    }

    render() {
        console.log(this.props)
        return (
            <Table borderless>
                <thead>
                <tr>
                    <th><Translate name={"barcode"}/></th>
                    <th><Settings size={20}></Settings></th>
                </tr>
                </thead>
                <tbody>
                {this.props.product.barcode && this.props.product.barcode.map(function (value) {
                    return (
                        <tr>
                            <td>{value.barcode}</td>
                            <td>
                                <Button size="sm" color="danger" className="mb-0">
                                    <Trash2 size={16}/>
                                </Button>
                            </td>
                        </tr>
                    )
                })}
                <tr>
                    <td>
                        <InputGroup size="sm">
                            <Input
                                type="text"
                                id="barcode"
                                value={this.state.currentInputValue ? this.state.currentInputValue || '' : ''}
                                onChange={event => this.setInputValue(event.target.value)}
                            />
                            <DropdownComponent
                                data={this.props.barcodeTypes}
                                onChange={this.props.setModalValues}
                                name='barcodeType'
                            />
                        </InputGroup>
                    </td>
                    <td>
                        <Button size="sm" color="primary" onClick={()=>{this.props.barcodeActions("add",this.state.currentInputValue);this.setInputValue("")}}>
                            <Plus size={16}/>
                        </Button>
                    </td>
                </tr>
                </tbody>

            </Table>
        );
    }
}
