import React from "react";
import classes from './cardBody.module.css'
import {CardBody, Col, Row} from "reactstrap";
import FormatListNumberedIcon from '@material-ui/icons/FormatListNumbered';
import Translate from "../../../../Translate";
import InputUi from "../../../../components/inputUI/inputUI";

const BodyComponent = props => {

    const changeFormHandler = (name, value, area) => {
        props.setFormDataValue(name, value, area)
    }

    const formDataRender = (data, area) => {
        let obj = []
        for (let item in data) {
            obj.push(
                <InputUi
                    key={Math.random()}
                    margin={'0 0 20px'}
                    label={item}
                    type={'number'}
                    name={item}
                    value={data[item]}
                    onChange={event => changeFormHandler(event.target.name, event.target.value, area)}
                />
            )
        }
        return obj;
    }

    return (
        <CardBody>
            <h4 className='pb-2'>
                <FormatListNumberedIcon fontSize={'large'}/>
                <Translate name={'It is necessary to indicate the numbers of the sections in the corresponding field'}/>
            </h4>
            <Row>
                <Col md={6} className={classes.colWindow}>
                    <div>
                        {
                            formDataRender(props.formData.departOne,'departOne')
                        }
                    </div>
                </Col>
                <Col md={6} className={classes.colWindow}>
                    <div>
                        {
                            formDataRender(props.formData.departTwo, 'departTwo')
                        }
                    </div>
                </Col>
            </Row>
        </CardBody>
    )
}

export default BodyComponent
