import React, {useState} from 'react';
import {Button, Card, CardBody, Col, Collapse, Row} from 'reactstrap';
import classes from './advancedSearch.module.css'
import Translate from "../../../../../Translate";
import SearchIcon from '@material-ui/icons/Search';
import CheckboxesUi from "../../../../../components/checkBoxUI/checkBoxUI";
import SwitchesUi from "../../../../../components/switchUI/switchUI";
import ButtonUi from "../../../../../components/buttons/buttonUi";
import SelectUi from "../../../../../components/selectUI/selectUI";
import TuneIcon from '@material-ui/icons/Tune';
import SettingsIcon from '@material-ui/icons/Settings';

const AdvancedSearch = (props) => {
    const [isOpen, setIsOpen] = useState(false);
    const [type, setType] = useState(false)

    const toggle = () => {
        setIsOpen(!isOpen)
        setType(!type)
    };

    return (
        <div>
            <Button className={classes.searchBtn} color={type ? 'success' : 'primary'} onClick={toggle}
                    style={{marginBottom: '1rem'}}>
                <Translate name={props.buttonName}/> <SearchIcon className={classes.icon}/>
            </Button>
            <Collapse isOpen={isOpen}>
                <Card>
                    <CardBody>
                        <Row>
                            <Col md={4} className={classes.searchWindow}>
                                <h5><Translate name={'Search settings'}/></h5>
                                <hr/>
                                {
                                    Object.keys(props.searchItemsKey).map(
                                        (item, index) => {

                                            return (
                                                <p key={index}>
                                                    <CheckboxesUi
                                                        checked={false}
                                                        label={item}
                                                        size={"small"}
                                                        color={"primary"}
                                                    />
                                                </p>
                                            )
                                        }
                                    )
                                }
                            </Col>
                            <Col md={3}>
                                <h5>
                                    <TuneIcon  className="mr-1"/>
                                    <Translate name={'filters'}/>
                                </h5>
                                <hr/>
                                <div className={classes.searchWindow}>
                                    <h6 className={classes.secondHeader}><Translate name={'groups'}/></h6>
                                    <ButtonUi
                                        width={'auto'}
                                        height={'auto'}
                                        name={<Translate name={'Classifiers'}/>}
                                        color={'primary'}
                                        variant={'contained'}
                                        padding={'2px 10px'}
                                        fontSize={'12px'}
                                        fontWeight={700}
                                    />
                                    {/*<RadioUi*/}
                                    {/*    headerName={'Groups'}*/}
                                    {/*    data={props.groups}*/}
                                    {/*    name={'groups'}*/}
                                    {/*    color={'primary'}*/}
                                    {/*    size={"small"}*/}
                                    {/*/>*/}
                                </div>
                                <h6 className={classes.secondHeader}><Translate name={'image'}/></h6>
                                <SwitchesUi
                                    label={'Image'}
                                />
                                <h6 className={classes.secondHeader}><Translate name={'status'}/></h6>
                                <SwitchesUi
                                    label={'active'}
                                />
                                <h6 className={classes.secondHeader}><Translate name={'suppliers'}/></h6>
                                <SwitchesUi
                                    label={'suppliers'}
                                />
                            </Col>
                            <Col md={5}>
                                <h5><Translate name={'Additional settings'}/></h5>
                                <hr/>
                                <div style={{verticalAlign: 'middle'}}>
                                    <span>Cuyc tal arajin</span>
                                    <SelectUi
                                        label={'value'}
                                    />
                                    <span>hatu</span>
                                </div>
                            </Col>
                        </Row>
                    </CardBody>
                </Card>
            </Collapse>
        </div>
    );
}

export default AdvancedSearch;
