import React, { useState } from 'react';
import {
    Card,
    CardBody,
    Col,
    Collapse,
    ListGroup,
    ListGroupItem,
    Row
} from 'reactstrap';
import SubGroupMenu from "./subGroupMenu"
import ModalComponent from "./subGroupModal"
import {ChevronDown, ChevronRight} from "react-feather";
import PlusButton from "../../../components/buttons/plusButton";
import EditButton from "../../../components/buttons/editButton";
import DeleteButton from "../../../components/buttons/deleteButton";
function GroupTableItem(props) {
    const [isOpen, setIsOpen] = useState(false);
    const toggle = () => setIsOpen(!isOpen);
    function Chevron(props) {
        if (props.data && props.data.length>0) {
            if (!isOpen) {
                return <ChevronRight size={25} className="mr-2" onClick={toggle}/>
            }
            return <ChevronDown size={25} className="mr-2" onClick={toggle}/>
        }
        return <span className="mr-4"></span>
    }
    return (
            <ListGroup className="py-0 my-1 rounded">
                <ListGroupItem active >
                    <Row className="todo-list-group-item">
                    <Col sm="11" md="11">
                        <Chevron data={props.data.subGroup}/>
                        {props.data.name}
                    </Col>
                    <Col sm="" md="1">
                        <PlusButton perm={props.perm} color="#fff" onClick={() => props.toggleModal('add',0,props.data.id)}/>
                        <EditButton perm={props.perm} onClick={function(){ props.toggleModal('edit',props.data.id); props.actions("get",props.data)}}/>
                        <DeleteButton perm={props.perm} onClick={() => props.toggleModal('delete',props.data.id)}/>
                    </Col>
                    </Row>
                </ListGroupItem>
                <Collapse  isOpen={isOpen}>
                    <Card  className="my-1">
                        <CardBody className="py-0">
                            <SubGroupMenu
                                data={ props.data.subGroup }
                                group_id = { props.data.id }
                            />

                        </CardBody>
                    </Card>
                </Collapse>
            </ListGroup>

    )

}
function GroupTable(props) {
    if(Object.keys(props.data).length>0){
            return <div>
                {Object.keys(props.data).map(key =>
                    <GroupTableItem key = {key} data={props.data[key]} toggleModal = {props.toggleModal} perm={props.perm} actions={props.groupActions}/>
                )}
            </div>
    }else{
        return <div></div>
    }

}

export default GroupTable;
