export const sidebarImage = img => ({
    type: 'BG_IMAGE',
    img
})

export const sidebarImageUrl = imgurl => ({
    type: 'BG_IMAGE_URL',
    imgurl
})

export const bgColorSuccess = () => ({
    type: 'BG_COLOR',
})

export const sidebarCollapsed = collapsed => ({
    type: 'SIDEBAR_COLLAPSED',
    collapsed
})

export const sidebarSize = size => ({
    type: 'SIDEBAR_SIZE',
    size
})

export function sidebarBgColor(color) {

    return dispatch => {
        localStorage.setItem('sidebarBgColor', sidebarBgColor);
        dispatch(bgColorSuccess())
    }
}
