import React, { useState } from 'react';
import {Breadcrumb, BreadcrumbItem, Card, CardBody, Collapse} from 'reactstrap';
import {ChevronDown, ChevronRight} from "react-feather";
function GroupTableItem(props) {
    const [isOpen, setIsOpen] = useState(false);
    const toggle = () => setIsOpen(!isOpen);
    function Chevron(props) {
        // if (props.data) {
            if (!isOpen) {
                return <ChevronRight size={30} className="mr-2" onClick={toggle}/>
            }
            return <ChevronDown size={30} className="mr-2" onClick={toggle}/>
        // }
        // return <div></div>
    }
    return (
        <div>
            <Breadcrumb>
                <Chevron />
                <BreadcrumbItem  onClick={toggle}>{props.data.name}</BreadcrumbItem>
            </Breadcrumb>
            <Collapse  isOpen={isOpen}>
                <Card>
                    <CardBody>
                        <ListGroup horizontal={breakpoint} className="my-2" key={idx}>
                            <ListGroup.Item>This ListGroup</ListGroup.Item>
                            <ListGroup.Item>renders horizontally</ListGroup.Item>
                            <ListGroup.Item>on {breakpoint}</ListGroup.Item>
                            <ListGroup.Item>and above!</ListGroup.Item>
                        </ListGroup>
                    </CardBody>
                </Card>
            </Collapse>


        </div>
    )

}
function GroupTable(props) {
    if(props.data.length>0){
            return <div>
                {props.data.map((value, index) => {
                    return (
                        <GroupTableItem key = {index} data={value} toggleModal = {props.toggleModal} perm={props.perm} actions={props.actions}/>
                    )
                })}
            </div>
    }else{
        return <div></div>
    }
    
}

export default GroupTable;
