import React, { useState } from "react";
import {Badge} from "reactstrap";
import {connect} from "react-redux";

function LocalizeTab(props){
    const [code, setCode] = useState(thsi.props.active);
    const handleClick = code =>{
        setCode(code)
    }
        return (
                <div className="badge-toolbar">

                    {props.languages.map(function (lang) {
                        return <Badge className={`cursor-pointer ${code === lang.code? `bg-primary text-white`:`bg-white font-weight-normal text-secondary `}`} onClick = {()=>handleClick(lang.code)}>
                            {lang.name}
                        </Badge>
                    })}
                </div>
        );
}

const mapStateToProps = state => {
    return {
        languages: state.languages.languages,
        active: state.languages.active

    }
};

export default connect(mapStateToProps)(LocalizeTab);
