import React from "react";
import {Col, Row, FormGroup, Label, Button, ModalBody, ModalFooter, CustomInput} from "reactstrap";
import {ChevronDown,ChevronRight} from 'react-feather';
import Translate from "../../../Translate";

const UserModal = (props) => {
    const pages = props.pages;

    function handleInputChange(key, value) {
        let new_value = {}
        new_value[key] = value
        props.setModalValues(new_value)
    }

    function handle(key,parent_id,id){
        console.log(key,parent_id,id)
        props.handle(key,parent_id,id)
    }

    function handleClick() {
        props.addUser(props.user)
    }

    console.log(props.pages)
    return (
        <React.Fragment>
            <ModalBody>
                <Row>
                    <Col md="12">
                        <FormGroup>
                            <Label for="firstName"><Translate name="name"/></Label>
                            <input
                                className="form-control"
                                type="text"
                                id="name"
                                onChange={event => handleInputChange("name", event.target.value)}
                                required
                            />
                        </FormGroup>
                    </Col>
                    <Col md="12">
                        <ul className="list-group icheck-task">
                            {Object.keys(pages).map(function(key) {
                                let checked = true;
                                if(pages[key].checked){
                                    checked =  pages[key].checked
                                }
                                return <li className={"list-group-item list-group-item-action no-border  bg-lighten-4"} key={key} >
                                    <Row className="todo-list-group-item">
                                        <Col sm="1" md="1"><Label check><CustomInput type="checkbox" defaultChecked={checked} onClick={() => props.handle('checked',key)}/></Label></Col>
                                        <Col sm="10" md="10"><p className="mb-0 font-small-3"><Translate name={pages[key].name}/></p></Col>
                                        <Col sm="1" md="1"><ChevronDown size={18} className="mr-2" onClick={()=>handle('open',key)} /></Col>
                                    </Row>
                                    <ul className="list-group icheck-task hide">
                                    {Object.keys(pages[key].tools).map(function(id) {
                                        let checked1 = true;
                                        if(pages[key].tools[id].checked){
                                            checked1 =  pages[key].checked
                                        }
                                        return <li className={"list-group-item list-group-item-action no-border  bg-lighten-4"} key={id}>
                                            <Row className="todo-list-group-item">
                                                <Label check onClick={()=>handle('checked',key,id)}><CustomInput  type="checkbox" defaultChecked={checked1} /></Label>
                                                <p className="mb-0 font-small-3"><Translate name={pages[key].tools[id].name}/></p>
                                            </Row>
                                        </li>

                                    })}
                                    </ul>
                                </li>
                            })}
                        </ul>
                    </Col>

                </Row>


            </ModalBody>
            <ModalFooter>
                <Button color="primary" type="submit" onClick={handleClick}>
                    <Translate name="confirm"/>
                </Button>
            </ModalFooter>
        </React.Fragment>
    );

};

export default UserModal;
