import { saveSession,getSession } from "../../utility/session";

import {
    LOGIN_REQUEST,
    LOGIN_REQUEST_FAIL,
    LOGIN_REQUEST_SUCCESS,
} from "./actionTypes";


const INIT_STATE = {
    authUser: getSession('auth'),
    loading: false,
    success: false,
    fail: false,
    errors: {},
};
console.log(getSession("auth"))

export default (state = INIT_STATE, action) => {
    switch (action.type) {
        case LOGIN_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: {},
            };
        case LOGIN_REQUEST_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,
                // errors: getResponseErrors(action.error.response),
            };
        case LOGIN_REQUEST_SUCCESS:

            saveSession('auth',JSON.parse(action.result.data),true);
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                authUser: action.result,
                errors: {},
            };


        default:
            return {...state};
    }
};
