import React from "react";
import {Col, Row, FormGroup, Label, Button, ModalBody, ModalFooter, ModalHeader, Modal} from "reactstrap";
import Translate from "../../../Translate";
import PositionDownMenu from "./positionDownMenu";

const PositionModal = (props) => {
    // const pages = props.pages;
    function handleInputChange(key, value) {
        props.setModalValues(key,value)
    }

    function toggle() {
        props.positionModal("add",!props.modal.add)
    }
    function handleClick() {
        props.addPosition(props.position)
    }

    return (
        <React.Fragment>
            <Modal isOpen={props.modal.add} toggle={()=>toggle()}  size="md">
                <ModalHeader toggle={()=>this.toggle()}><Translate name="addPosition"/></ModalHeader>

            <ModalBody>
                <Row>
                    <Col md="12">
                        <FormGroup>
                            <Label for="name"><Translate name="name"/></Label>
                            <input
                                className="form-control"
                                type="text"
                                id="name"
                                value={props.position.name}
                                onChange={event => handleInputChange("name", event.target.value)}
                                required
                            />
                        </FormGroup>
                    </Col>
                    <Col md="12">
                        <PositionDownMenu data = { props.pages } perm={ props.position.perm  } handle = { props.handle }/>
                    </Col>

                </Row>


            </ModalBody>
            <ModalFooter>
                <Button color="primary" type="submit" onClick={handleClick}>
                    <Translate name="confirm"/>
                </Button>
            </ModalFooter>
            </Modal>
        </React.Fragment>
    );

};

export default PositionModal;
