import React, {Component} from "react";
import {connect} from "react-redux";
import {bindActionCreators} from "redux";
import {setTranslations} from './redux/lang/actions'
import SessionStorage from "./services/SessionStorage";


class Translate extends Component {

    componentDidMount() {
        if (!this.props.translations[this.props.name]) {
            if (2) {
                let index = false;
                let lang = SessionStorage.get('lang') || "am";
                for (let item of this.props.translations) {
                    if (item.key === this.props.name && item.value === this.props.name && item.language === lang) {
                        index = true;
                    }
                }
                if (index === false) {
                    this.props.setTranslations(this.props.name, this.props.name, lang);
                }
            }
        }
    }

    contentRender = () => {
        if (parseInt(this.props.translations[this.props.name]) / 1) {
            return this.props.translations[this.props.name]
        } else {
            return this.props.translations[this.props.name] ?
                this.props.translations[this.props.name]
                :
                this.props.name
        }
    }

    render() {

        return (
            <span>
                {
                    this.contentRender()
                }
        </span>
        )
    }
}

const mapDispatchToProps = dispatch => {
    return bindActionCreators(
        {
            setTranslations
        },
        dispatch
    );
};

const mapStateToProps = state => {
    return {
        translations: state.languages.translations,
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Translate);
