import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { getUsers } from "../../redux/users/actions";
import { addUser } from "../../redux/users/actions";
import { Button, Col, Card, CardBody, Modal, ModalHeader} from "reactstrap";
import TableComponent from '../../components/table/tableComponent';
import AddUser from './addUser';
// import AddUser from './addUser';
import Translate from "../../Translate";



class UserContainer extends Component {
    constructor() {
        thi.state = {
            modal:false,
        }
        this.props.getUsers()
    }
    toggle() {
        this.setState({
            modal: !this.state.modal
        });
    }
    render() {
        console.log(this.state)
        return(
            <Col sm="12">
                <Card>
                    <CardBody>
                        <Button
                            color="primary"
                            type="button"
                            className="btn btn-raised btn-danger btn-block my-2 shadow-z-2"
                            onClick={this.toggle}
                        >
                            <Translate name="Add"/>
                        </Button>{" "}
                        <TableComponent data = {this.props.users}/>
                    </CardBody>
                </Card>
                <Modal isOpen={this.state.modal} toggle={this.toggle}  size="md">
                    <ModalHeader toggle={this.toggle}>Add Contact</ModalHeader>

                </Modal>
            </Col>
        )
    }
}

const mapDispatchToProps = dispatch => {
    return bindActionCreators(
        {
            getUsers,addUser
        },
        dispatch
    );
};

const mapStateToProps = state => {
    return state.users
};
export default connect(mapStateToProps, mapDispatchToProps)(UserContainer);
