import React, {Component} from "react";
import {Card, CardBody, Col, Row} from "reactstrap";
import CustomizedSnackbars from "../../../components/snachbarsUI/snachbarsUi";
import HeaderComponent from "./cardHeader/cardHeader";

class ImportPage extends Component {
    state = {
        exel: false,
        file: null,
        errorType: null,
        uploadText: ''
    }

    handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }

        this.setState({
            exel: false,
        })
    };

    importExelHandler = event => {
        let file = event.target.files[0];
        if (file.type === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet') {
            this.setState({
                exel: true,
                file: event.target.files[0],
                uploadText: 'your file added successfully',
                errorType: 'success'
            })
        } else {
            event.target.value = '';
            this.setState({
                exel: true,
                file: null,
                uploadText: 'file type is not supported, make sure your file is Excel',
                errorType: 'error'
            })
        }

    }

    render() {

        return (
            <Row>
                <Col sm={12}>
                    <Card>
                        <HeaderComponent
                            file={this.state.file}
                            onChange={this.importExelHandler}
                        />
                        <CardBody>
                        </CardBody>
                        <CustomizedSnackbars
                            open={this.state.exel}
                            severity={this.state.errorType}
                            text={this.state.uploadText}
                            handleClose={this.handleClose}
                        />
                    </Card>
                </Col>
            </Row>
        )
    }
}

export default ImportPage
