import React, {Component} from "react";
import {
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem
} from "reactstrap";
import ReactCountryFlag from "react-country-flag";
// import { connect } from "react-redux";
// import { bindActionCreators } from "redux";
import { initialize,addTranslation,getTranslations,setActiveLanguage } from 'react-localize-redux';
import {languages} from "../../redux/lang/languages";
import {translations} from "../../redux/lang/translation";
import {store} from '../../redux/store';



class ThemeNavbar extends Component {
    constructor(props) {
        super(props);
        // this.state = {
        //     languages:languages,
        //     translations:translations
        // }
        const languages = [
            { name: 'English', code: 'en' },
            { name: 'French', code: 'fr' },
            { name: 'Spanish', code: 'es' }
        ];
        store.dispatch(initialize({languages:languages}, { defaultLanguage: 'fr' }));
        addTranslation(translations)
    }

    render() {
        // let state = this.state
        // console.log(languages)
        return (
            <UncontrolledDropdown nav inNavbar className="pr-1">
                <DropdownToggle nav>
                    <ReactCountryFlag code="us" svg/> EN
                </DropdownToggle>
                <DropdownMenu right>

                </DropdownMenu>
            </UncontrolledDropdown>

        );
    }
}
//
// function mapStateToProps(state) {
//     return state;
// }
//
// const mapDispatchToProps = dispatch => {
//     return bindActionCreators(
//         {
//             setLanguage
//         },
//         dispatch
//     );
// };

// export default connect(mapStateToProps, mapDispatchToProps)(ThemeNavbar);
export default ThemeNavbar;
