import React,{Component} from "react";
import {Col, Row, Form, FormGroup, Label, Button, ModalBody, ModalFooter, Input} from "reactstrap";
import Translate from "../../Translate";
import jwt from "jwt-simple";

// const AddContact = ({ id, props }) => {

class AddContact extends Component {

    state = {
        firstname: "",
        lastname: "",
        username: "",
        password: "",
        position: "",
        active: ""
    };
    // let firstname, lastname, username, password, position, active;
    // let user = props.user[0]
    // if(user){
    //     firstname = user.firstname;
    //     lastname = user.lastname;
    // }
    handleInputChange(key, value) {
        console.log(key, value)
        this.setState({key:value})
    }

    // console.log(props.user)
    render() {
        console.log(this.props)
        return (
            <React.Fragment>
                <ModalBody>
                    <Row>
                        <Col md={6}>
                            <FormGroup>
                                <Label for="firstName"><Translate name="firstname"/></Label>
                                <input
                                    className="form-control"
                                    type="text"
                                    name="firstname"
                                    id="firstName"
                                    onChange={event => this.handleInputChange("firstname", event.target.value)}
                                    // ref={node => (firstname = node)}
                                    value={this.state.firstname}
                                    required
                                />
                            </FormGroup>
                        </Col>
                        <Col md={6}>
                            <FormGroup>
                                <Label for="lastName"><Translate name="lastname"/></Label>
                                <input
                                    className="form-control"
                                    type="text"
                                    name="lastname"
                                    id="lastName"
                                    onChange={event => this.handleInputChange("lastname", event.target.value)}
                                    // ref={node => (lastname = node)}
                                />
                            </FormGroup>
                        </Col>
                    </Row>
                    <Row>
                        <Col md={6}>
                            <FormGroup>
                                <Label for="username"><Translate name="username"/></Label>
                                <input
                                    className="form-control"
                                    type="text"
                                    name="username"
                                    id="username"
                                    onChange={event => this.handleInputChange("username", event.target.value)}
                                    // ref={node => (username = node)}
                                    required
                                />
                            </FormGroup>
                        </Col>
                        <Col md={6}>
                            <FormGroup>
                                <Label for="password"><Translate name="password"/></Label>
                                <input
                                    className="form-control"
                                    type="password"
                                    name="password"
                                    id="password"
                                    onChange={event => this.handleInputChange("username", event.target.value)}
                                    // ref={node => (password = node)}
                                    required
                                />
                            </FormGroup>
                        </Col>
                    </Row>
                    <Row>
                        <Col md={6}>
                            <FormGroup>
                                <Label for="position"><Translate name="position"/></Label>
                                <Input type="select" id="position" name="position" onChange={event => this.handleInputChange("position", event.target.value)}>
                                    {this.props.positions.map((item, key) => {
                                        return <option value={item.id} key={key}>{item.name}</option>
                                    })}
                                </Input>
                            </FormGroup>
                        </Col>
                        <Col md={6}>
                            <FormGroup>
                                <Label for="active"><Translate name="active"/></Label>
                                <Input type="select" id="active" name="active" onChange={event => this.handleInputChange("active", event.target.value)}>
                                    <option value="0">Այո</option>
                                    <option value="1">Ոչ</option>
                                </Input>
                            </FormGroup>
                        </Col>
                    </Row>

                </ModalBody>
                <ModalFooter>
                    <Button color="primary" type="submit">
                        <Translate name="add"/>
                    </Button>
                </ModalFooter>
            </React.Fragment>
        );
    }

};

export default AddContact;
