import React, { useState } from 'react';
import { UncontrolledDropdown,Dropdown, DropdownMenu, DropdownToggle } from 'reactstrap';

const DropdownComponent = (props) => {
    const [dropdownOpen, setDropdownOpen] = useState(false);

    const toggle = () => setDropdownOpen(prevState => !prevState);

    return (
        <UncontrolledDropdown isOpen={dropdownOpen} toggle={toggle}>
            <DropdownToggle caret aria-expanded={dropdownOpen}>
                Dropdown
            </DropdownToggle>
            <DropdownMenu>
                {props.data.map((value) => {
                    return <DropdownItem onClick={toggle}>{value.name}</DropdownItem>

                })}

            </DropdownMenu>
        </UncontrolledDropdown>

    );
}

export default DropdownComponent;
