import React, {Component} from "react";
import {
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem
} from "reactstrap";
import ReactCountryFlag from "react-country-flag";
import { renderToStaticMarkup } from "react-dom/server";
import { withLocalize } from "react-localize-redux";
import globalTranslations from "./translations/global.json";



class ThemeNavbar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            languages:languages,
            translations:translations
        }
        initialize({languages:languages}, { defaultLanguage: 'am' })
        addTranslation(translations)
    }

    render() {
        let state = this.state
        console.log(languages)
        return (
            <UncontrolledDropdown nav inNavbar className="pr-1">
                <DropdownToggle nav>
                    <ReactCountryFlag code="us" svg/> EN
                </DropdownToggle>
                <DropdownMenu right>
                    {languages.map((value, index) =>
                        <DropdownItem key={value.code} onClick={() => {
                            setActiveLanguage(value.code)
                            console.log(getTranslations(state)['add'])
                        }}>
                            <ReactCountryFlag code={value.code} svg/> {value.name}
                        </DropdownItem>
                    )}
                </DropdownMenu>
            </UncontrolledDropdown>

        );
    }
}
export default withLocalize(ThemeNavbar);
