import {ADD_FILE, SET_CHECK_TOUCHED_VALUE, SET_FORM_DATA_VALUE} from "./actionTypes";

export function addFile(name, value) {

    return {
        type: ADD_FILE,
        name,
        value
    }
}

export function checkTouched(area, id) {

    return (dispatch, getState) => {
        const formData = {...getState().importReducer.formData};


        formData[area].forEach(
            item => {
                if (item.id === id) {
                    item.touched = true
                }
            }
        )
        dispatch(setCheckTouched(formData))
    }
}

export function setCheckTouched(formData) {

    return {
        type: SET_CHECK_TOUCHED_VALUE,
        formData
    }
}

export function changeFormDataValue(name, value, area, id) {

    return (dispatch, getState) => {
        const formDataValue = {...getState().importReducer.formDataValue};
        const formData = {...getState().importReducer.formData};
        formData[area].forEach(
            item => {
                if (item.id === id) {
                    if (value.length > 0) {
                        if (value / 1) {
                            item.valid = true;
                            item.error = false
                        } else {
                            item.valid = false;
                            item.error = 'value is invalid'
                        }
                    } else {
                        item.valid = false;
                        item.error = 'value is empty'
                    }
                }
            }
        )
        formDataValue[name] = value
        dispatch(setFormDataValue(formDataValue, formData))
    }
}

export function setFormDataValue(formDataValue, formData) {

    return {
        type: SET_FORM_DATA_VALUE,
        formDataValue,
        formData
    }
}
