import React, {Component} from "react";
import {
    TabContent,
    TabPane,
    Nav,
    NavItem,
    NavLink,
    Row,
    Col, FormGroup, Label, Input
} from "reactstrap";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import classnames from "classnames";
import Translate from "../../../Translate";
import MultiSelect from "../../../components/select/multiSelect"
import {RenameKeys, ObjectToArray} from "../../../utility/utils"
import GroupTabContent from "../group/subGroupMenu"

class TabsBorderBottom extends Component {

    constructor(props) {
        super(props)
        this.state = {
            activeTab: "code",
            modal: false
        };
        this.props.supplierActions("getAll");
        this.props.groupActions("getAll");

    }
    toggle = tab => {
        if (this.state.activeTab !== tab) {
            this.setState({
                activeTab: tab
            });
        }
    };

    render() {
        return (
            <div>
                <Nav tabs className="nav-border-bottom">
                    {Object.keys(this.props.groups).map((key) => {
                        return <NavItem>
                            <NavLink
                                className={classnames({
                                    active: this.state.activeTab === key
                                })}
                                onClick={() => {
                                    this.toggle(key);
                                }}
                            >
                                {this.props.groups[key].name[this.props.lang.active]}
                            </NavLink>
                        </NavItem>
                    })}

                    <NavItem>
                        <NavLink
                            className={classnames({
                                active: this.state.activeTab === "suppliers"
                            })}
                            onClick={() => {
                                this.toggle("suppliers");
                            }}
                        >
                            <Translate name={"suppliers"}/>
                        </NavLink>
                    </NavItem>

                    <NavItem>
                        <NavLink
                            className={classnames({
                                active: this.state.activeTab === "code"
                            })}
                            onClick={() => {
                                this.toggle("code");
                            }}
                        >
                            <Translate name={"code"}/>
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink
                            className={classnames({
                                active: this.state.activeTab === "description"
                            })}
                            onClick={() => {
                                this.toggle("description");
                            }}
                        >
                            <Translate name={"description"}/>
                        </NavLink>
                    </NavItem>
                </Nav>
                <TabContent activeTab={this.state.activeTab}>

                    {Object.keys(this.props.groups).map((key) => {
                        return (
                            <TabPane tabId={key}>
                                <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle1}>
                                    <DropdownToggle caret>
                                        This dropdown's menu is right-aligned
                                    </DropdownToggle>
                                    <DropdownMenu right>
                                        <GroupTabContent
                                            data={this.props.groups[key].subGroup}
                                            lang={this.props.lang}
                                            handleOpen={this.props.handleOpen}
                                            selectGroup={this.props.selectGroup}
                                            selected ={this.props.selectedGroups}
                                            page="products"
                                        />
                                    </DropdownMenu>
                                </Dropdown>

                            </TabPane>
                        )
                    })}

                    <TabPane tabId="suppliers">
                        <MultiSelect
                            name={"suppliers"}
                            handleChange={this.props.setModalValues}
                            data={
                                RenameKeys(
                                    ObjectToArray(this.props.suppliers),
                                    ['name', 'name'],
                                    ['value', 'label']
                                )
                            }
                        />
                    </TabPane>
                    <TabPane tabId="code">
                        <Row>
                            <Col sm="12">
                                <FormGroup>
                                    <Label for="projectinput2">Բարկոդ</Label>
                                    <Input type="text" id="projectinput2" name="lname"/>
                                </FormGroup>
                            </Col>
                        </Row>
                    </TabPane>
                    <TabPane tabId="description">
                        <Row>
                            <Col sm="12">
                                <FormGroup>
                                    <Label for="projectinput8">Նկարագրություն</Label>
                                    <Input type="textarea" id="projectinput8" rows="5" name="comment"/>
                                </FormGroup>
                            </Col>
                        </Row>
                    </TabPane>
                </TabContent>
                <Modal
                    isOpen={this.state.modal}
                    toggle={this.toggle}
                    className={this.props.className}
                >
                    <ModalHeader toggle={this.toggle}>Modal title</ModalHeader>
                    <ModalBody>
                        Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed
                        do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                        Ut enim ad minim veniam, quis nostrud exercitation ullamco
                        laboris nisi ut aliquip ex ea commodo consequat. Duis aute
                        irure dolor in reprehenderit in voluptate velit esse cillum
                        dolore eu fugiat nulla pariatur. Excepteur sint occaecat
                        cupidatat non proident, sunt in culpa qui officia deserunt
                        mollit anim id est laborum.
                    </ModalBody>
                    <ModalFooter>
                        <Button color="primary" onClick={this.toggle}>
                            Do Something
                        </Button>{" "}
                        <Button color="secondary" onClick={this.toggle}>
                            Cancel
                        </Button>
                    </ModalFooter>
                </Modal>
            </div>
        );
    }
}

export default TabsBorderBottom;
