import React, {useState} from "react";
import {
    Col,
    FormGroup,
    Label,
    Button,
    ModalBody,
    ModalFooter,
    Modal,
    ModalHeader
} from "reactstrap";
import Translate from "../../../Translate";
import {Plus,Trash2} from "react-feather"

const SupplierModal = (props) => {
    function PhoneInput() {
        let element = [];
        let length = props.supplier.phone?props.supplier.phone.length:1;
        function buttonType(index){
            return(index+1===length)?
                 <Plus size={20} className="success" onClick={() => props.setModalValues("phone", "", index, true)} />:
                 <Trash2 size={20} className="danger" onClick={() => props.setModalValues("phone", "", index, false)} />
        }
        for (let i=0;i<length;i++){
            element.push(
                <FormGroup row>
                    <Label for="Phone" sm={3}>{i==0?<Translate name={"phone"}/>:""}</Label>
                    <Col sm={9}>
                        <div className="position-relative has-icon-right">
                            <input
                                className={`form-control `}
                                type="text"
                                id="Phone"
                                value={props.supplier.phone?props.supplier.phone[i]:""}
                                onChange={event => props.setModalValues("phone", event.target.value,i)}
                            />
                            <div className="form-control-position">
                                {buttonType(i)}
                            </div>
                        </div>
                    </Col>
                </FormGroup>
            )

        }
        return element;


    }
    function modalBodyContent() {
        if (props.type === "delete") {
            return (
                <ModalBody>Դուք համոզված ե՞ք ջնջել</ModalBody>
            )
        } else {

            return (

                <ModalBody>
                    <form>
                        <FormGroup row>

                            <Label for="Name" sm={3}><Translate name="name"/>:</Label>
                            <Col sm={7} className="pr-0">
                                <input
                                    className={`form-control rounded-left ${props.errors.name ? 'is-invalid' : ''}`}
                                    type="text"
                                    id="firstName"
                                    value={props.supplier.name}
                                    onChange={event => props.setModalValues("name", event.target.value)}
                                />
                            </Col>
                            <Col sm={2} className="pl-0 ">
                                <select name="" className="form-control  rounded-right" data-type="name_type">
                                    <option value="1">ՍՊԸ</option>
                                    <option value="2">ՓԲԸ</option>
                                    <option value="3">ԲԲԸ</option>
                                    <option value="4">ԱՁ</option>
                                </select>
                            </Col>
                        </FormGroup>

                        <FormGroup row>
                            <Label for="HH" sm={3}><Translate name="hh"/>:</Label>
                            <Col sm={9}>
                                <input
                                    className={`form-control  ${props.errors.hh ? 'is-invalid' : ''}`}
                                    type="text"
                                    id="HH"
                                    value={props.supplier.hh}
                                    onChange={event => props.setModalValues("hh", event.target.value)}
                                />
                            </Col>
                        </FormGroup>

                        <FormGroup row>
                            <Label for="hvhh" sm={3}><Translate name="hvhh"/>:</Label>
                            <Col sm={9}>
                                <input
                                    className={`form-control  ${props.errors.hvhh ? 'is-invalid' : ''}`}
                                    type="text"
                                    id="HVHH"
                                    value={props.supplier.hvhh}
                                    onChange={event => props.setModalValues("hvhh", event.target.value)}
                                />
                            </Col>
                        </FormGroup>

                        <FormGroup row>
                            <Label for="Address" sm={3}><Translate name="address"/>:</Label>
                            <Col sm={9}>
                                <input
                                    className={`form-control  ${props.errors.address ? 'is-invalid' : ''}`}
                                    type="text"
                                    id="Address"
                                    value={props.supplier.address}
                                    onChange={event => props.setModalValues("address", event.target.value)}
                                />
                            </Col>
                        </FormGroup>

                        <FormGroup row>
                            <Label for="Bank" sm={3}><Translate name="bank"/>:</Label>
                            <Col sm={7} className="pr-0 ">
                                <input
                                    className={`form-control  ${props.errors.bank ? 'is-invalid' : ''}`}
                                    type="text"
                                    id="Bank"
                                    value={props.supplier.bank}
                                    onChange={event => props.setModalValues("bank", event.target.value)}
                                />
                            </Col>
                            <Col sm={2} className="pl-0 ">
                                <select name="currency" className="form-control ">
                                    <option value="1">AMD</option>
                                    <option value="2">USD</option>
                                    <option value="3">RUR</option>
                                    <option value="4">GEL</option>
                                    <option value="5">GBP</option>
                                </select>
                            </Col>
                        </FormGroup>

                        <FormGroup row>
                            <Label for="Director" sm={3}><Translate name="director"/>:</Label>
                            <Col sm={9}>
                                <input
                                    className={`form-control  ${props.errors.director ? 'is-invalid' : ''}`}
                                    type="text"
                                    id="Director"
                                    value={props.supplier.director}
                                    onChange={event => props.setModalValues("director", event.target.value)}
                                />
                            </Col>
                        </FormGroup>
                        {/*<FormGroup row>*/}
                        {/*    <Label for="Phone" sm={3}><Translate name="phone"/>:</Label>*/}
                        {/*    <Col sm={9}>*/}
                        {/*        <div className="position-relative has-icon-right">*/}
                        {/*            <input*/}
                        {/*                className={`form-control  ${props.errors.phone ? 'is-invalid' : ''}`}*/}
                        {/*                type="text"*/}
                        {/*                id="Phone"*/}
                        {/*                value={props.supplier.phone?props.supplier.phone[0]:""}*/}
                        {/*                onChange={event => props.setModalValues("phone", event.target.value,0)}*/}
                        {/*            />*/}
                        {/*            <div className="form-control-position">*/}
                        {/*                <Plus size={20} className="success"  onClick={()=>props.setModalValues("phone", "",1)} />*/}
                        {/*            </div>*/}
                        {/*        </div>*/}
                        {/*    </Col>*/}
                        {/*</FormGroup>*/}
                        {PhoneInput()}

                    </form>

                </ModalBody>
            )

        }

    }

    return (
        <React.Fragment>
            <Modal isOpen={props.modal[props.type]} toggle={() =>{ props.toggleModal(props.type)}} size="lg">
                <ModalHeader toggle={() => {props.toggleModal(props.type)}}><Translate
                    name={props.type + "Supplier"}/></ModalHeader>
                {modalBodyContent()}
                <ModalFooter>
                    <Button color="primary" type="submit" onClick={function () {
                        props.supplierActions(props.type, props.supplier);
                    }}>
                        <Translate name="confirm"/>
                    </Button>
                </ModalFooter>
            </Modal>

        </React.Fragment>
    );

};

export default SupplierModal;
