import React from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
// import './index.css';
import { Menu, Icon } from 'antd';

const {SubMenu} = Menu;

function SubMenu1(props) {

    // const [open, setOpen] = useState(false);
    //
    // function handleOpen() {
    //     setOpen(!open)
    // }
    //
    function SubMenuItem(props) {
        if (props.data.children) {
            return <SubMenu key="sub3" title={props.name}>
                <SubMenuItem data={props.data.children}/>
            </SubMenu>
        }else{
            return <Menu.Item key="5">{props.name}</Menu.Item>
        }
    }

    // function Chevron(props) {
    //     if (props.data) {
    //         if (!open) {
    //             return <ChevronRight size={18} className="mr-2" onClick={() => handleOpen()}/>
    //         }
    //         return <ChevronDown size={18} className="mr-2" onClick={() => handleOpen()}/>
    //     }
    //     return <div></div>
    // }

    if (props.data.length > 0) {
        return <Menu
            mode="inline"
            // openKeys={this.state.openKeys}
            onOpenChange={this.onOpenChange}
            style={{ width: 256 }}
        >
            {props.data.map((value, index) => {
                return <SubMenuItem data = {value}/>
            })}

        </Menu>


    } else {
        return <div></div>
    }
};
export default class Sider extends React.Component {
    // submenu keys of first level
    rootSubmenuKeys = ['sub1', 'sub2', 'sub4'];

    state = {
        openKeys: ['sub1'],
    };

    onOpenChange = openKeys => {
        const latestOpenKey = openKeys.find(key => this.state.openKeys.indexOf(key) === -1);
        if (this.rootSubmenuKeys.indexOf(latestOpenKey) === -1) {
            this.setState({ openKeys });
        } else {
            this.setState({
                openKeys: latestOpenKey ? [latestOpenKey] : [],
            });
        }
    };

    render() {
        return (
            <SubMenu1 data = {this.props.data} />
        );
    }
}
