import React, {Component} from "react";
import {Col, FormGroup, Input, Label, Nav, NavItem, NavLink, Row, TabContent, TabPane,} from "reactstrap";
import classnames from "classnames";
import Translate from "../../../../../Translate";
import MultiSelect from "../../../../../components/select/multiSelect"
import {ObjectToArray, RenameKeys} from "../../../../../utility/utils";
import GroupModal from "./group/groupModal";
// import DropdownComponent from "../../../../../components/dropdown/dropdown";
// import {setModalValues} from "../../../../../redux/products/actions";
import CodeContent from "./code/content";
import UploadButton from "../../../../../components/buttons/upploadBtnUI";
import classes from './productTab.module.css';
import ButtonUi from "../../../../../components/buttons/buttonUi";
import RestoreFromTrashIcon from '@material-ui/icons/RestoreFromTrash';
import AddToHomeScreenIcon from '@material-ui/icons/AddToHomeScreen';

class TabsBorderBottom extends Component {

    constructor(props) {
        super(props)
        this.state = {
            activeTab: "code",
            modal: false,
            key: null,
            btnType: null,
            id: null,
            valImg: null
        };
        this.props.supplierActions("getAll");
        this.props.groupActions("getAll");

    }

    toggle1 = () => {
        this.setState({
            modal: !this.state.modal
        });
    }
    toggle = tab => {
        if (this.state.activeTab !== tab) {
            this.setState({
                activeTab: tab
            });
        }
    };

    setGroupId = id => {
        this.setState({
            key: id
        });
    }

    uploadImageHandler = event => {
        this.setState({
            valImg: event.target.files
        })
        this.props.SetUploadImages(event.target.name, event.target.files)
    }
    deleteImageHandler = (elem, index) => {
        console.log(elem, index)
    }

    imagesRender = () => {
        let imgData = this.state.valImg
        let imgObject = []
        if (imgData) {
            imgData.forEach(
                (item, index) => {
                    imgObject.push(
                        <Col
                            key={item.size + Math.random()}
                            md={3}
                        >
                            <div className={classes.imageWindow}>
                                <div className={classes.imgBord}>
                                    <img className={classes.imgBordImage} src={URL.createObjectURL(item)}
                                         alt={item.name}/>
                                    <div className={classes.conttroller}/>
                                    <div className={classes.btns}>
                                        <div className={classes.conttrollerBtnsMain}>
                                            <ButtonUi
                                                disabled={true}
                                                color={'primary'}
                                                variant={"contained"}
                                                padding={'5px'}
                                                width={'auto'}
                                                height={'auto'}
                                            >
                                                <AddToHomeScreenIcon/>
                                            </ButtonUi>
                                        </div>
                                        <div className={classes.conttrollerBtnsDelete}>
                                            <ButtonUi
                                                onClick={this.deleteImageHandler.bind(this, item, index)}
                                                color={'secondary'}
                                                variant={"contained"}
                                                padding={'5px'}
                                                width={'auto'}
                                                height={'auto'}
                                            >
                                                <RestoreFromTrashIcon/>
                                            </ButtonUi>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </Col>
                    )
                }
            )
        }
        return imgObject;
    }

    render() {
        return (
            <div>
                <Nav tabs className="nav-border-bottom">
                    <NavItem>
                        <NavLink
                            className={classnames({
                                active: this.state.activeTab === "classifiers"
                            })}
                            onClick={() => {
                                this.toggle("classifiers");
                            }}
                        >
                            <Translate name={'classifiers'}/>
                        </NavLink>
                    </NavItem>


                    <NavItem>
                        <NavLink
                            className={classnames({
                                active: this.state.activeTab === "suppliers"
                            })}
                            onClick={() => {
                                this.toggle("suppliers");
                            }}
                        >
                            <Translate name={"suppliers"}/>
                        </NavLink>
                    </NavItem>

                    <NavItem>
                        <NavLink
                            className={classnames({
                                active: this.state.activeTab === "code"
                            })}
                            onClick={() => {
                                this.toggle("code");
                            }}
                        >
                            <Translate name={"code"}/>
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink
                            className={classnames({
                                active: this.state.activeTab === "description"
                            })}
                            onClick={() => {
                                this.toggle("description");
                            }}
                        >
                            <Translate name={"description"}/>
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink
                            className={classnames({
                                active: this.state.activeTab === "images"
                            })}
                            onClick={() => {
                                this.toggle("images");
                            }}
                        >
                            <Translate name={"images"}/>
                        </NavLink>
                    </NavItem>
                </Nav>
                <TabContent activeTab={this.state.activeTab}>
                    <TabPane tabId="classifiers">
                        {
                            Object.keys(this.props.groups).map(
                                (item, index) => {
                                    return (
                                        <Row
                                            key={index}
                                        >
                                            <Col md={3} style={{padding: '11px'}}>
                                                <b>
                                                    {this.props.groups[item].name}
                                                </b>
                                            </Col>
                                            <Col md={9}>
                                                <GroupModal
                                                    getSubGroups={this.props.getSubGroups}
                                                    data={this.props.subGroups}
                                                    productGroups={this.props.productGroups}
                                                    group={this.props.groups[item]}
                                                    name={this.props.groups[item].name}
                                                    lang={this.props.lang}
                                                    handleOpen={this.props.handleOpen}
                                                    selectGroup={this.props.selectGroup}
                                                    selected={this.props.selectedGroups}
                                                    setModalValues={this.props.setModalValues}
                                                    product={this.props.product}
                                                    id={item}
                                                    dataId={this.props.groups[item].id}
                                                />
                                            </Col>
                                        </Row>
                                    )
                                }
                            )
                        }
                    </TabPane>

                    <TabPane tabId="suppliers">
                        <MultiSelect
                            name={"suppliers"}
                            handleChange={this.props.setModalValues}
                            data={
                                RenameKeys(
                                    ObjectToArray(this.props.suppliers),
                                    ['name', 'name'],
                                    ['value', 'label']
                                )
                            }
                        />
                    </TabPane>
                    <TabPane tabId="code">
                        <Row>
                            <Col sm="12">

                                <FormGroup>
                                    <CodeContent {...this.props} />
                                </FormGroup>

                            </Col>
                        </Row>
                    </TabPane>
                    <TabPane tabId="description">
                        <Row>
                            <Col sm="12">
                                <FormGroup>
                                    <Label for="description"><Translate name={'description'}/></Label>
                                    <Input
                                        type="textarea"
                                        id="description"
                                        rows="5"
                                        value={this.props.product.description ? this.props.product.description || '' : ''}
                                        onChange={event => this.props.setModalValues("description", event.target.value)}
                                    />
                                </FormGroup>
                            </Col>
                        </Row>
                    </TabPane>
                    <TabPane tabId="images">
                        <header className="d-flex justify-content-center p-1">
                            <FormGroup encType="multipart/form-data">
                                <UploadButton
                                    color={'primary'}
                                    variant={'outlined'}
                                    name={'upImages'}
                                    onChange={this.uploadImageHandler}
                                />
                            </FormGroup>
                        </header>
                        <section className="p-1">
                            <Row>
                                {this.imagesRender()}
                            </Row>
                        </section>
                    </TabPane>
                </TabContent>

            </div>
        );
    }
}

export default TabsBorderBottom;
