            //         index = true;
            //     }
            // }
            // if (index === false) {
            //     console.log("index", index, props.name.toLowerCase(), props.name, lang)
            //     // props.setTranslations(props.name.toLowerCase(), props.name, lang);
            // }
        }
        return props.name;
    }
    return (
        <span>
            {checkWord(props.translations)}
        </span>
    );
}

const mapDispatchToProps = dispatch => {
    return bindActionCreators(
        {
            setTranslations
        },
        dispatch
    );
};

const mapStateToProps = state => {
    return {
        translations: state.languages.translations,
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Translate);
