const initialState = {
    sidebarBgColor: false
}

export default function sidebarBgColor(state = initialState, action) {

    switch (action.type) {

        case 'BG_COLOR':
            return {
                sidebarBgColor: localStorage.getItem('bgColor')
            };
        default: return state;
    }
}

