import React, {Component} from "react";
import {
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem
} from "reactstrap";
import ReactCountryFlag from "react-country-flag";
import { renderToStaticMarkup } from "react-dom/server";
import { withLocalize,initialize } from "react-localize-redux";
import {translations} from "../../redux/lang/translation";
import {store} from "../../redux/store"


class ThemeNavbar extends Component {
    constructor(props) {
        super(props);

        const languages = [
            { name: 'English', code: 'en' },
            { name: 'French', code: 'fr' },
            { name: 'Spanish', code: 'es' }
        ];
        store.dispatch(initialize(languages));
        this.props.addTranslation(translations)
    }
    componentWillMount() {
        // console.log(this.props)
    }

    render() {
        let state = this.state
        // console.log(this.props.getTranslations())
        return (
            <UncontrolledDropdown nav inNavbar className="pr-1">
                <DropdownToggle nav>
                    <ReactCountryFlag code="us" svg/> EN
                </DropdownToggle>
                <DropdownMenu right>
                    {/*{languages.map((value, index) =>*/}
                    {/*    <DropdownItem key={value.code} onClick={() => {*/}
                    {/*        setActiveLanguage(value.code)*/}
                    {/*        console.log(getTranslations(state)['add'])*/}
                    {/*    }}>*/}
                    {/*        <ReactCountryFlag code={value.code} svg/> {value.name}*/}
                    {/*    </DropdownItem>*/}
                    {/*)}*/}
                </DropdownMenu>
            </UncontrolledDropdown>

        );
    }
}
export default withLocalize(ThemeNavbar);
