import React, {Component} from "react";
import {
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem
} from "reactstrap";
import ReactCountryFlag from "react-country-flag";
// import { connect } from "react-redux";
// import { bindActionCreators } from "redux";
import { initialize,addTranslation,getTranslate,setActiveLanguage } from 'react-localize-redux';
import {languages} from "../../redux/lang/languages";
import {translations} from "../../redux/lang/translation";

class ThemeNavbar extends Component {
    constructor(props) {
        super(props);
        this.state = {
        }
        initialize({languages:languages}, { defaultLanguage: 'am' })

    }

    render() {
        let props = this.props.localize
        console.log(props)
        return (
            <UncontrolledDropdown nav inNavbar className="pr-1">
                <DropdownToggle nav>
                    <ReactCountryFlag code="us" svg/> EN
                </DropdownToggle>
                <DropdownMenu right>
                    {props.languages.map((value, index) =>
                        <DropdownItem key={value.code} onClick={() => {
                            setActiveLanguage(value.code)
                        }}>
                            <ReactCountryFlag code={value.code} svg/> {value.name}
                        </DropdownItem>
                    )}
                </DropdownMenu>
            </UncontrolledDropdown>

        );
    }
}
