import React from "react";
import {Button, FormGroup, Label, Modal, ModalBody, ModalFooter, ModalHeader} from "reactstrap";
import Translate from "../../../Translate";

const CategoryModal = (props) => {
    console.log(props)
    function handleInputChange(key, value) {
        let new_value = {}
        new_value[key] = value
        props.setModalValues(new_value)
    }
    function toggle(type,id) {
        this.props.categoryModal(type,!this.props.modal[type])
        this.props.setModalValues({'parent_id':id})
        if(type==="edit" && id){
            this.props.getCategory(id)
        }
    }
    function handleClick() {
        props.addCategory(props.category)
    }

    return (
        <React.Fragment>
            <Modal isOpen={props.modal[props.type]} toggle={()=>toggle(props.type)}  size="md">
                <ModalHeader toggle={()=>toggle(props.type)}><Translate name="addCategory"/></ModalHeader>
                <ModalBody>
                    <FormGroup>
                        <Label for="name"><Translate name="name"/></Label>
                        <input
                            className="form-control"
                            type="text"
                            id="name"
                            value={props.category.name}
                            onChange={event => handleInputChange("name", event.target.value)}
                            required
                        />
                    </FormGroup>
                    <FormGroup>
                        <Label for="name_en"><Translate name="name_en"/></Label>
                        <input
                            className="form-control"
                            type="text"
                            id="name_en"
                            value={props.category.name_en}
                            onChange={event => handleInputChange("name_en", event.target.value)}
                            required
                        />
                    </FormGroup>
                    <FormGroup>
                        <Label for="name_ru"><Translate name="name_ru"/></Label>
                        <input
                            className="form-control"
                            type="text"
                            id="name_ru"
                            value={props.category.name_ru}
                            onChange={event => handleInputChange("name_ru", event.target.value)}
                            required
                        />
                    </FormGroup>
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" type="submit" onClick={handleClick}>
                        <Translate name="confirm"/>
                    </Button>
                </ModalFooter>
            </Modal>

        </React.Fragment>
    );

};

export default CategoryModal;
