import React, {Component} from "react";
import {connect} from "react-redux";
import {bindActionCreators} from "redux";
import {setTranslations} from './redux/lang/actions'
import SessionStorage from "./services/SessionStorage";
let obj = [];

class Translate extends Component {


    componentWillUnmount() {
        // if (!this.props.translations[this.props.name.toLowerCase()]) {
        //     if (this.props.status) {
        //         let lang = SessionStorage.get('lang') || "am";
        //         if (this.props.translations.length > 0) {
        //             let index = false;
        //             for (let item of this.props.translations) {
        //                 if (item.value === this.props.name) {
        //                     index = true;
        //                 }
        //             }
        //             if (index === false) {
        //                 console.log('key: ', this.props.name.toLowerCase(), 'name: ', this.props.name, 'lnag: ', lang)
        //                 // this.props.setTranslations(this.props.name, this.props.name, lang);
        //             }
        //         } else {
        //             // this.props.setTranslations(this.props.name, this.props.name, lang);
        //         }
        //     }
        // }
        let lang = SessionStorage.get('lang') || "am";
        if (obj.length > 0) {
            let index = false;
            for (let item of obj) {
                if (item.value === this.props.name) {
                    index = true;
                }
            }
            if (index === false) {
                this.push({'key': this.props.name.toLowerCase(), 'name': this.props.name, 'lnag': lang})
                // this.props.setTranslations(this.props.name, this.props.name, lang);
            }
        } else this.push({'key': this.props.name.toLowerCase(), 'name': this.props.name, 'lnag': lang});
console.log(obj);

    }


    contentRender = () => {
        let word = null;
            if (this.props.name) {
                if (this.props.translations.length > 0) {
                    if (this.props.translations[(this.props.name.toLowerCase())]) {
                        console.log("name", this.props.translations[this.props.name.toLowerCase()])
                        word = this.props.translations[this.props.name.toLowerCase()]
                    }
                } else {
                    word = this.props.name
                }
            }
        return word;
    }

    render() {

        return (
            <span>
                {this.props.translations[this.props.name]?this.props.translations[this.props.name]:this.props.name}
        </span>
        )
    }
}

const mapDispatchToProps = dispatch => {
    return bindActionCreators(
        {
            setTranslations
        },
        dispatch
    );
};

const mapStateToProps = state => {
    return {
        translations: state.languages.translations,
        status: state.languages.status
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Translate);
