import React from "react";
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import 'react-bootstrap-table/dist/react-bootstrap-table-all.min.css';
import EditButton from "../buttons/editButton";
import DeleteButton from "../buttons/deleteButton";

class PaginationTable extends React.Component {
    state = {
        index: false,
        options: {
            afterSearch: this.afterSearch,
        },
        selectRowProp: {
            mode: 'radio',
            bgColor: '#868e96',
            clickToSelect: true,
            onSelect: this.onRowSelect,
        }
    }

    afterSearch = (searchText, result) => {
    }

    onRowSelect = (...obj) => {
        console.log(obj)
    }


    render() {
        return (
            <div>
                {
                    this.state.index ?
                        <EditButton
                            perm={'Edit'}
                            onClick={() => alert('Edit')}
                        />
                        :
                        null
                }
                {
                    this.state.index ?
                        < DeleteButton
                            perm={'Delete'}
                            onClick={
                                () => alert('Delete')
                            }
                        />
                        :
                        null
                }
                <BootstrapTable
                    ref='table'
                    data={this.props.data}
                    pagination
                    selectRow={this.state.selectRowProp}
                    search={true}
                    options={this.state.options}
                >
                    <TableHeaderColumn dataSort={true} dataField='id' isKey>#</TableHeaderColumn>
                    <TableHeaderColumn dataSort={true} dataField='key'>{this.props.dataKey}</TableHeaderColumn>
                    <TableHeaderColumn dataSort={true} dataField='value'>{this.props.value}</TableHeaderColumn>
                    <TableHeaderColumn dataField='language'>{this.props.lang}</TableHeaderColumn>
                    {/*<TableHeaderColumn dataField='button'>{this.props.buttons}</TableHeaderColumn>*/}
                </BootstrapTable>
            </div>
        );
    }
}

export default PaginationTable
