import React, {Component, useState} from "react";
import { ListGroup, ListGroupItem} from "reactstrap";
import {Minus, Plus} from "react-feather";
import Translate from "../../../Translate";

function SubMenu(props) {
    const [open, setOpen] = useState(false);
    function handleOpen() {
        setOpen(!open)
    }
    function SubMenuItem(props) {
        if (props.data) {
            return <SubMenu data={props.data}/>
        }
        return <div></div>
    }
    function Chevron(props) {
        if(props.data) {
            if (!open) {
                return <Plus size={18} className="mr-2 float-left" onClick={() => handleOpen()}/>
            }
            return <Minus size={18} className="mr-2 float-left" onClick={() => handleOpen()}/>
        }
        return <div></div>
    }

        return <ListGroupItem disabled  tag="a" href="#">
            {props.data.name}
            <Chevron data={props.data.children}/>
            <SubMenuItem data={props.data.children}/>

        </ListGroupItem>
};

class ListGroupDisabled extends Component {
    constructor(props) {
        super(props)

    }

    render() {
        if (this.props.data.length > 0) {
            return <ListGroup flush>
                {this.props.data.map((value, index) => {
                    return <SubMenu data = {value}/>
                })}

            </ListGroup>

        } else {
            return <div></div>
        }
    }
}

export default ListGroupDisabled;
