import React, {Component, useEffect, useState} from 'react';
import {
    Button,
    ButtonGroup,
    Card,
    CardBody,
    Col,
    Collapse,
    ListGroup,
    ListGroupItem,
    Row
} from 'reactstrap';
import SubGroupMenu from "./subGroupMenu"
import classes from './content.module.css'
import {ChevronDown, ChevronRight} from "react-feather";
import PlusButton from "../../../components/buttons/plusButton";
import EditButton from "../../../components/buttons/editButton";
import DeleteButton from "../../../components/buttons/deleteButton";
import * as Icon from "react-feather"
import TextFields from "../../../components/textFieldUI/textField";
import Translate from "../../../Translate";
import CustomizedTreeView from "../../../components/treeUi/tree";

class ContentTable extends Component {

    clickHandler = id => {
        this.props.getSeletGroup(id)
        this.props.getSubGroup(id)
    }

    componentDidMount() {
        this.props.getSubGroup(this.props.group.id)
        // window.setSub = setTimeout(
        //     () => this.props.group.id ? this.props.getSubGroup(this.props.groups[0].id) : 0,
        //     1000
        // )
    }

    componentWillUnmount() {
        // clearTimeout(window.setSub)
    }

    render() {
        return (
            <>

                <header className={classes.header}>
                    {
                        this.props.groups ?
                            this.props.groups.map(
                                item => {

                                    return (
                                        <React.Fragment key={item.id}>
                                            <Button
                                                active={
                                                    this.props.group.id === item.id ?
                                                        true
                                                        :
                                                        false
                                                }
                                                className={classes.groupBtn}
                                                outline
                                                color='primary'
                                                onClick={
                                                    () => this.clickHandler(item.id)
                                                }
                                            >
                                                {item.name}
                                            </Button>
                                        </React.Fragment>
                                    )
                                }
                            )
                            :
                            null
                    }
                </header>
                <nav className={classes.nav}>
                    <div className={classes.controllers}>
                        {
                            this.props.group.id ?
                                <PlusButton/>
                                :
                                null
                        }
                        {
                            this.props.group.id ?
                                <EditButton
                                    perm={this.props.perm}
                                    onClick={() => {
                                        this.props.toggleModal('edit', this.props.group.id);
                                        this.props.groupActions("get", this.props.group)
                                    }}
                                />
                                :
                                null
                        }
                        {
                            this.props.group.id ?
                                <DeleteButton
                                    perm={this.props.perm}
                                    onClick={
                                        () => this.props.toggleModal('delete', this.props.group.id)
                                    }
                                />
                                :
                                null
                        }
                    </div>
                    <TextFields
                        label={<Translate name={'Search'}/>}
                    />
                </nav>
                <section className={classes.section}>
                    {
                        this.props.group.id ?
                            <CustomizedTreeView
                                mainName={this.props.group.name}
                                data={this.props.subGroups}
                            />
                            :
                            null
                    }
                </section>
            </>
        )
    }
}

export default ContentTable
