import React, {Component} from "react";
import { setActiveLanguage } from 'react-localize-redux';

import {
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem
} from "reactstrap";
import ReactCountryFlag from "react-country-flag";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { set_active_language } from "../../redux/auth/actions";

class ThemeNavbar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            languages: [
                {code: "am", value: 'Armenian'},
                {code: "ru", value: 'Russian'},
                {code: "us", value: 'English'},
            ]
        };
    }

    render() {
        return (
            <UncontrolledDropdown nav inNavbar className="pr-1">
                <DropdownToggle nav>
                    <ReactCountryFlag code="us" svg/> EN
                </DropdownToggle>
                <DropdownMenu right>
                    {this.state.languages.map((value, index) =>
                        <DropdownItem key={value.code} onClick={() => {
                            set_active_language(value.code)

                        }}>
                            <ReactCountryFlag code={value.code} svg/> {value.value}
                        </DropdownItem>
                    )}
                </DropdownMenu>
            </UncontrolledDropdown>

        );
    }
}

export default ThemeNavbar;
