import qs from "qs";
import SessionStorage from "./SessionStorage";
import axios from "axios";

export default class ApiClient {
    configs = {};
    defaultConfigs = {};
    API_URI = process.env.REACT_APP_API_URL;

    constructor() {
        const token = SessionStorage.get("token");
        let headers = {
            "Content-Type": "application/json"
        };

        if (token) {
            headers["token"] = token;
        }

        this.defaultConfigs = {
            headers,
            params: {}
        };

        this.resetConfigs();
    }

    getRuntimeConfigs() {
        const token = SessionStorage.get("token");
        let headers = {
            "Content-Type": "application/json"
        };

        if (token) {
            headers["token"] = token;
        }

        return Object.assign({}, this.configs, {
            headers
        });
    }

    resetConfigs() {
        this.configs = Object.assign({}, this.defaultConfigs);
    }

    setConfig(key, value) {
        console.log(key, value);
        this.configs = Object.assign({}, this.configs, {
            [key]: value
        });
    }

    mergeConfigs(params, headers, configs) {
        const runtimeConfigs = this.getRuntimeConfigs();
        let responseType = {};
        if (params && params.responseType) {
            responseType = params.responseType;
        }
        return Object.assign(
            {},
            runtimeConfigs,
            Object.assign(
                {},
                configs,
                {
                    ...responseType,
                    params: Object.assign({}, runtimeConfigs.params, params),
                    headers: Object.assign({}, runtimeConfigs.headers, headers),
                    paramsSerializer: function(params) {
                        return qs.stringify(params, { arrayFormat: "brackets" });
                    }
                }
            )
        );
    }

    static cancelToken() {
        const CancelToken = axios.CancelToken;
        return CancelToken.source();
    }

    get(uri, params = {}, headers = {}, configs = {}) {

        return axios.get(this.API_URI + `?action="get"&data=${params}&path=`+uri + "",
            this.mergeConfigs(params, headers, configs)
        ).then(response => response.data);
        // let token_request = "";
        // if (token == 1){
        //     token_request = '&token='+sessionStorage.getItem("token");
        // }
        // let company =  {};
        // company.id = 0;
        // company = JSON.stringify(company);
        // let url =  "http://192.168.1.196:8099/api/index.php";
        // return fetch(
        //     url + `?action="get"&data=${params}&path="Login/Login"` + token_request + `&company=${company}`,
        //     {
        //         method: "GET",
        //         dataType: "json",
        //         headers: {
        //             Accept: "application/json",
        //             "Content-Type": "application/json"
        //         },
        //         mode: "cors", // no-cors, cors, *same-origin
        //         credentials: "same-origin"
        //     }
        // );
    }

    post(uri, data, params = {}, headers = {}, configs = { hasFile: false }) {
        let formData = new FormData();

        if (configs.hasFile) {
            for (let key in data) {
                if (Array.isArray(data[key])) {
                    for (let i = 0; i < data[key].length; i++) {
                        formData.append(`${key}[]`, data[key][i]);
                    }
                } else {
                    formData.set(key, data[key]);
                }
            }
            data = formData;
        }
        console.log(this.mergeConfigs(params, headers, configs));
        return axios.post(`${this.API_URI}/${uri}`, data,
            this.mergeConfigs(params, headers, configs)
        ).then(response => response.data);
    }

    put(uri, data, params = {}, headers = {}, configs = { hasFile: false }) {
        let formData = new FormData();

        if (configs.hasFile) {
            for (let key in data) {
                if (Array.isArray(data[key])) {
                    for (let i = 0; i < data[key].length; i++) {
                        formData.append(`${key}[]`, data[key][i]);
                    }
                } else {
                    formData.set(key, data[key]);
                }
            }
            data = formData;
        }
        return axios.put(`${this.API_URI}/${uri}`, data,
            this.mergeConfigs(params, headers, configs)
        ).then(response => response.data);
    }

    delete(uri, params = {}, headers = {}, configs = {}) {
        return axios.delete(`${this.API_URI}/${uri}`,
            this.mergeConfigs(params, headers, configs)
        ).then(response => response.data);
    }

    upload(uri, form, data, params = {}, headers = {}, configs = { multiple: false }) {
        let files = form.files;
        if (!Array.isArray(files)) {
            files = new Array(files);
        }
        let formData = new FormData();
        // formData.append('files', files);
        files.forEach((item, i) => {
            formData.append("data[" + i + "][file]", item);
            formData.append("data[" + i + "][type]", form.type);
            formData.append("data[" + i + "][caption]", "caption " + i);
            formData.append("data[" + i + "][credit]", "credit " + i);
        });

        return this.post(uri, formData, params, headers, configs);
    }
}
